package com.inyaw.base;import lombok.Setter;import lombok.experimental.Accessors;@Setter@Accessors(chain = true)public class BaseDto {    private Integer pageNum = 1;    private Integer pageSize = 10;    public Integer getPageNum() {        if (pageNum > 0) {            return pageNum - 1;        }        return pageNum;    }    public Integer getPageSize() {        return pageSize;    }}