package com.inyaw.blog.controller;import com.inyaw.base.BaseResult;import com.inyaw.blog.bean.TagInfo;import com.inyaw.blog.service.TagInfoService;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/tag")@RequiredArgsConstructorpublic class TagInfoController {    private final TagInfoService tagInfoService;    @PostMapping("/save")    public BaseResult save(@RequestBody TagInfo tag) {        tagInfoService.save(tag);        return BaseResult.success();    }    @GetMapping("/list")    public BaseResult list() {        return BaseResult.success(tagInfoService.list());    }    @PostMapping("/delete")    public BaseResult delete(@RequestBody TagInfo tag) {        tagInfoService.removeById(tag);        return BaseResult.success();    }}