package com.inyaw.blog.service;import com.inyaw.base.BaseResult;import com.inyaw.blog.bean.BlogInfo;import com.inyaw.blog.bean.BlogTag;import com.inyaw.blog.dao.BlogInfoMapper;import com.inyaw.blog.dao.BlogTagMapper;import com.inyaw.blog.dto.BlogInfoDto;import com.inyaw.blog.vo.InyawBlogVo;import com.inyaw.blog.vo.InyawBlogWebInfoVo;import com.inyaw.blog.vo.InyawBlogWebVo;import com.mybatisflex.core.paginate.Page;import com.mybatisflex.core.query.QueryChain;import com.mybatisflex.core.query.QueryWrapper;import lombok.RequiredArgsConstructor;import org.springframework.beans.BeanUtils;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.time.LocalDateTime;import java.util.ArrayList;import java.util.List;import static com.inyaw.blog.bean.table.BlogInfoTableDef.BLOG_INFO;@Service@RequiredArgsConstructor@Transactionalpublic class BlogInfoService {    private final BlogInfoMapper blogInfoMapper;    private final BlogTagMapper blogTagMapper;    public List<BlogInfo> findAll(BlogInfo req) {        QueryWrapper queryWrapper = QueryWrapper.create().orderBy(BLOG_INFO.CREATE_TIME.desc()).where(BLOG_INFO.TITLE.eq(req.getTitle()));        return blogInfoMapper.selectListByQuery(queryWrapper);    }    public Page<BlogInfo> findListPage(BlogInfoDto req) {        QueryWrapper queryWrapper = QueryWrapper.create().orderBy(BLOG_INFO.CREATE_TIME.desc()).where(BLOG_INFO.TITLE.eq(req.getTitle()));        BlogInfo params = new BlogInfo();        BeanUtils.copyProperties(req, params);        return blogInfoMapper.paginate(req.getPageNum(), req.getPageSize(), queryWrapper);    }    public List<InyawBlogWebVo> findWebList(BlogInfo req) {        return QueryChain.of(blogInfoMapper).select(BLOG_INFO.ALL_COLUMNS)                .from(BLOG_INFO).where(BLOG_INFO.TITLE.eq(req.getTitle()))                .orderBy(BLOG_INFO.CREATE_TIME.desc()).listAs(InyawBlogWebVo.class);    }    public Page<InyawBlogWebVo> findWebListPage(BlogInfo req) {        return QueryChain.of(blogInfoMapper).select(BLOG_INFO.ALL_COLUMNS)                .from(BLOG_INFO).where(BLOG_INFO.TITLE.eq(req.getTitle()))                .orderBy(BLOG_INFO.CREATE_TIME.desc()).pageAs(new Page<>(req.getPageNum(), req.getPageSize()), InyawBlogWebVo.class);    }    @Transactional    public void save(BlogInfoDto req) {        BlogInfo blog = new BlogInfo();        BeanUtils.copyProperties(req, blog);        if (req.getId() != null) {            blog.setUpdateTime(LocalDateTime.now());        } else {            blog.setCreateTime(LocalDateTime.now());            blog.setViews(0);            blog.setComments(0);        }        blogInfoMapper.insert(blog);        if (req.getTagList() != null && !req.getTagList().isEmpty()) {            List<BlogTag> blogTagList = new ArrayList<>();            req.getTagList().forEach(tagInfo -> {                BlogTag blogTag = new BlogTag();                blogTag.setTagId(blog.getId());                blogTag.setBlogId(tagInfo.getId());                blogTagList.add(blogTag);            });            blogTagMapper.insertBatch(blogTagList);        }    }    public void delete(BlogInfo req) {        blogInfoMapper.deleteById(req.getId());    }    public BlogInfo getBlogInfo(BlogInfo req, boolean isUpdate) {        BlogInfo blog = blogInfoMapper.selectOneById(req.getId());        if (isUpdate) {            blog.setViews(blog.getViews() + 1);            blogInfoMapper.insert(blog);        }        return null;    }    public InyawBlogWebInfoVo getWebBlogInfo(BlogInfo req) {        InyawBlogWebInfoVo vo = new InyawBlogWebInfoVo();        BlogInfo bean = getBlogInfo(req, true);        BeanUtils.copyProperties(bean, vo);        BlogInfo last = blogInfoMapper.findTopByIdLessThanOrderByCreateTimeDesc(vo.getId());        if (last != null) {            InyawBlogVo lastVo = new InyawBlogVo();            BeanUtils.copyProperties(last, lastVo);            vo.setPreviousBlog(lastVo);        }        BlogInfo next = blogInfoMapper.findTopByIdGreaterThanOrderByCreateTimeAsc(vo.getId());        if (next != null) {            InyawBlogVo nextVo = new InyawBlogVo();            BeanUtils.copyProperties(next, nextVo);            vo.setNextBlog(nextVo);        }        return vo;    }    public BaseResult<List<BlogInfo>> archive() {        BlogInfo params = new BlogInfo();        params.setStatus(true);        List<BlogInfo> list = findAll(params);        return BaseResult.success(list);    }}