package com.inyaw.sys.controller;import com.inyaw.base.BaseResult;import com.inyaw.config.CacheService;import com.inyaw.sys.service.SysConfigService;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.*;import java.util.Map;@RestController@RequestMapping("/config")@RequiredArgsConstructorpublic class SysConfigController {    private final SysConfigService sysConfigService;    private final CacheService cacheService;    @PostMapping("/save")    public BaseResult<String> save(@RequestBody Map<String, String> req) {        sysConfigService.save(req);        return BaseResult.success();    }    @GetMapping("/findAll")    public BaseResult<Map<String, Object>> getConfig(Integer type) {        Map<String, Object> config = cacheService.getConfig(type);        return BaseResult.success(config);    }}