package com.inyaw.sys.controller;import com.inyaw.base.BaseResult;import com.inyaw.config.CacheService;import com.inyaw.sys.bean.SysEnmu;import com.inyaw.sys.service.SysEnmuService;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.*;import java.util.Map;@RestController@RequestMapping("/config")@RequiredArgsConstructorpublic class SysEnmuController {    private final SysEnmuService sysEnmuService;    private final CacheService cacheService;    @PostMapping("/save")    public BaseResult save(@RequestBody SysEnmu sysEnmu) {        sysEnmuService.save(sysEnmu);        return BaseResult.success();    }    @GetMapping("/findAll")    public BaseResult getConfig(String module) {        Map<String, Object> config = cacheService.getConfig(module);        return BaseResult.success(config);    }}